import { useState } from "react";
import { FaCheck, FaEdit, FaPlus, FaSearch, FaTrash } from "react-icons/fa";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const TaskManager = () => {
  const [input, setInput] = useState("");
  const [tasks, setTasks] = useState([]);
  const [search, setSearch] = useState("");
  const [editIndex, setEditIndex] = useState(null);

  // Add or Update Task
  const handleAddTask = () => {
    if (!input.trim()) {
      toast.error("Task cannot be empty!");
      return;
    }

    if (editIndex !== null) {
      // Update task
      const updatedTasks = [...tasks];
      updatedTasks[editIndex].taskName = input;
      setTasks(updatedTasks);
      setEditIndex(null);
      toast.success("Task updated successfully!");
    } else {
      // Add new task
      const newTask = { taskName: input, isDone: false };
      setTasks([...tasks, newTask]);
      toast.success("Task added successfully!");
    }

    setInput("");
  };

  // Mark as Done
  const handleDone = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].isDone = !updatedTasks[index].isDone;
    setTasks(updatedTasks);
    toast.info(
      updatedTasks[index].isDone
        ? "Task marked as completed!"
        : "Task marked as not completed!"
    );
  };

  // Edit Task
  const handleEdit = (index) => {
    setInput(tasks[index].taskName);
    setEditIndex(index);
  };

  // Delete Task
  // Delete Task
// Delete Task
const handleDelete = (index) => {
  const updatedTasks = tasks.filter((_, i) => i !== index);

  // If the deleted task is the one being edited, reset edit state + input
  if (editIndex === index) {
    setInput("");
    setEditIndex(null);
  } else if (editIndex > index) {
    // Shift editIndex back if a task before it was deleted
    setEditIndex(editIndex - 1);
  }

  setTasks(updatedTasks);
  toast.error("Task deleted!");
};



  // Filtered tasks for search
  const filteredTasks = tasks.filter((task) =>
    task.taskName.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container my-4 p-4 border rounded shadow-sm bg-light d-flex flex-column">
      {/* Title */}
      <h1 className="text-center mb-4">📝 Task Manager App</h1>

      {/* Input Section */}
      <div className="d-flex mb-3">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          className="form-control me-2"
          placeholder="Enter a new task..."
        />
        <button className="btn btn-primary" onClick={handleAddTask}>
          <FaPlus /> {editIndex !== null ? "Update" : "Add"}
        </button>
      </div>

      {/* Search Section */}
      <div className="input-group mb-4">
        <span className="input-group-text">
          <FaSearch />
        </span>
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="form-control"
          placeholder="Search tasks..."
        />
      </div>

      {/* Task List */}
      <div className="list-group">
        {filteredTasks.length === 0 ? (
          <div className="text-center text-muted">No tasks found</div>
        ) : (
          filteredTasks.map((task, index) => (
            <div
              key={index}
              className="list-group-item d-flex justify-content-between align-items-center"
            >
              <span
                className={
                  task.isDone
                    ? "text-decoration-line-through text-muted"
                    : ""
                }
              >
                {task.taskName}
              </span>
              <div>
                <button
                  className="btn btn-sm btn-success me-2"
                  onClick={() => handleDone(index)}
                >
                  <FaCheck /> {task.isDone ? "Undo" : "Done"}
                </button>
                <button
                  className="btn btn-sm btn-warning me-2 text-white"
                  onClick={() => handleEdit(index)}
                >
                  <FaEdit /> Edit
                </button>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => handleDelete(index)}
                >
                  <FaTrash /> Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Toastify Container */}
      <ToastContainer position="top-center" autoClose={2000} />
    </div>
  );
};

export default TaskManager;
