import { useState } from "react";
import { FaCheck, FaEdit, FaPlus, FaSearch, FaTrash } from "react-icons/fa";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const TaskManager = () => {
    const [input,setInput] = useState('');
    const handleAddTask = ()=>{
        const obj={
            taskName:input,
            isDone:false
        }
    }
  return (
    <div className="container my-4 p-4 border rounded shadow-sm bg-light d-flex flex-column">
      {/* Title */}
      <h1 className="text-center mb-4">📝 Task Manager App</h1>

      {/* Input Section */}
      <div className="d-flex mb-3">
        <input
          type="text"
          value={input}
          onChange={(e)=>setInput(e.target.value)}
          className="form-control me-2"
          placeholder="Enter a new task..."
        />
        <button className="btn btn-primary" onClick={handleAddTask} >
          <FaPlus /> Add
        </button>
      </div>

      {/* Search Section */}
      <div className="input-group mb-4">
        <span className="input-group-text">
          <FaSearch />
        </span>
        <input
          type="text"
          className="form-control"
          placeholder="Search tasks..."
        />
      </div>

      {/* Task List */}
      <div className="list-group">
        {/* Active Task */}
        <div className="list-group-item d-flex justify-content-between align-items-center">
          <span>Buy Groceries</span>
          <div>
            <button className="btn btn-sm btn-success me-2">
              <FaCheck /> Done
            </button>
            <button className="btn btn-sm btn-warning me-2 text-white">
              <FaEdit /> Edit
            </button>
            <button className="btn btn-sm btn-danger">
              <FaTrash /> Delete
            </button>
          </div>
        </div>

        {/* Completed Task */}
        <div className="list-group-item d-flex justify-content-between align-items-center">
          <span className="text-decoration-line-through text-muted">
            Completed Task
          </span>
          <div>
            <button className="btn btn-sm btn-success me-2">
              <FaCheck /> Done
            </button>
            <button className="btn btn-sm btn-warning me-2 text-white">
              <FaEdit /> Edit
            </button>
            <button className="btn btn-sm btn-danger">
              <FaTrash /> Delete
            </button>
          </div>
        </div>
      </div>

      {/* Toastify Container */}
      <ToastContainer position="top-center" />
    </div>
  );
};

export default TaskManager;
